import React from 'react';
import Card from '@mui/material/Card';

import './largeCard.css';

function largeCard({title, quote}) {
  return (
    <Card sx={{ width: '40vh' , height: '45vh', backgroundColor: '#A61C3C', borderRadius: '10%', border: '5px solid black', borderColor: '#FF9B71', transition: 'transform 0.2s ease-in-out',
      '&:hover': {borderColor: '#208AAE', transform: 'scale(1.05)', boxShadow: '0 0 15px 5px #208AAE', }}}>
      <div>{title}</div>
    </Card>
  );
}

export default largeCard;
