import React from 'react';
import Card from '@mui/material/Card';
import Typography from '@mui/material/Typography';



import './largeCard.css';

function largeCard({title, quote, size}) {
  return (
    <Card sx={{ width: '40vh' , height: '45vh', backgroundColor: '#A61C3C', borderRadius: '10%', border: '5px solid black', borderColor: '#FF9B71', transition: 'transform 0.25s ease-in-out', boxShadow: '20px 20px 5px rgba(0, 0, 0, 0.5)', 
      '&:hover': {borderColor: '#208AAE', transform: 'scale(1.05)', boxShadow: '0 0 20px 2px #208AAE', cursor: 'pointer' }}}>

      <Typography sx={{ fontSize: 14, color: '#D8D78F', fontFamily: 'Rubik, sans-serif'}} gutterBottom>
          {title}
      </Typography>
    </Card>
  );
}

export default largeCard;
